"use strict";const slides=document.querySelectorAll(".slide"),btnLeft=document.querySelector(".carousel__btn--left"),btnRight=document.querySelector(".carousel__btn--right");slides.forEach(((e,t)=>e.style.transform=`translateX(${100*t}%)`));let curSlide=0,maxSlide=slides.length;const goSlide=function(){slides.forEach(((e,t)=>e.style.transform=`translateX(${100*(t-curSlide)}%)`))};btnRight.addEventListener("click",(function(){curSlide===maxSlide-1?curSlide=0:curSlide++,goSlide()})),btnLeft.addEventListener("click",(function(){0===curSlide?curSlide=maxSlide-1:curSlide--,goSlide()}));